import { URI } from '@vscode-alt/monaco-editor/esm/vs/base/common/uri';
export interface ICoreBaseStat {
    /**
     * The unified resource idnentifier of this file or folder.
     */
    resource: URI | string;
    /**
     * The name which is the last segement
     * of the {{path}}
     */
    name?: string;
    title?: string;
    /**
     * The last modification date represented
     * as millis from unix epoch.
     */
    mtime?: number;
    /**
     * A unique identifier that represents the
     * current state of the file or directory.
     */
    etag?: string;
    /**
     * The size of the file is known.
     */
    size?: number;
}
/**
 * A file resource with meta information.
 */
export interface ICoreFileStat extends ICoreBaseStat {
    /**
     * The resource is a directory. If {{true}}
     * {{encoding}} has no meaning
     */
    isDirectory: boolean;
    /**
     * Return {{true}} when this is a directory
     * that is not empty.
     */
    hasChildren: boolean;
    /**
     * The children of the file stat or undefined if none.
     */
    children?: ICoreFileStat[];
}
