

export interface RegisterFormatterEvent {
    selector: string;
    formatter: LabelRules;
}

export interface LabelRules {
    uri: {
        label: string; // myLabel:/${path}
        separator: '/' | '\\' | '';
        tildify?: boolean;
        normalizeDriveLetter?: boolean;
        authorityPrefix?: string;
    };
    workspace?: {
        suffix: string;
    };
}
